import "../Navigation/Navigation.css";
import React from "react";
import { Link } from "react-router-dom";
import Burger from "../Burger/Burger";

function Navigation({ isLoggedIn }) {

    const [isBurgerOpen, setIsBurgerOpen] = React.useState(false);
    // const [isMenuShown, setIsMenuShown] = React.useState(false);

    // console.log(isBurgerOpen)

    React.useEffect(() => {
      const body = document.querySelector("body");
      console.log(body)
      isBurgerOpen
        ? body.classList.add("body_locked")
        : body.classList.remove("body_locked");
    }, [isBurgerOpen]);

    const handleBurgerClick = () => {
      isBurgerOpen ? setIsBurgerOpen(false) : setIsBurgerOpen(true);
    };

    // return (
    //     <div className="navigation">
    //         <Switch>
    //             {!isLoggedIn}
    //         </Switch>
    //     </div>
    // )


    // function handleCloseMenuButtonClick() {
    //     setIsMenuShown(false);
    // }

    // function handleOpenMenuButtonClick() {
    //     setIsMenuShown(true);
    // }

    return (
        <div className={`menu ${isLoggedIn ? '' : 'menu_position_right'}`}>
            <div className={`menu__movies ${isLoggedIn ? '' : 'no-display'}`}>
                <Link to="/movies" className="menu__link menu__link_type_movies">Фильмы</Link>
                <Link to="/saved-movies" className="menu__link menu__link_type_movies">Сохранённые фильмы</Link>
            </div>
            <div className="menu__profile">

                <Link to="/signup" className={`menu__link ${isLoggedIn ? 'no-display' :
                    'menu__link_type_register'}`}>Регистрация</Link>
                {isLoggedIn ? <Link to="/profile" className="menu__link menu__link_type_account">Аккаунт</Link> :
                    <Link to="/signin" className="menu__link menu__link_type_login">Войти</Link>}

            </div>

            <Burger onClick={handleBurgerClick} isBurgerOpen={isBurgerOpen} />
            <div className={`menu__burgeris ${isBurgerOpen} ?  visible' : ''`}>

                <div className={isBurgerOpen ? 'header__burger-menu-container visible' : 'header__burger-menu-container'}>
                    <div className="header__burger-menu-links">
                        <Link to="/" className="header__burger-menu-link">Главная</Link>
                        <Link to="/movies" className="header__burger-menu-link">Фильмы</Link>
                        <Link to="/saved-movies" className="header__burger-menu-link">Сохранённые фильмы</Link>
                        <Link to="/profile" className="header__burger-menu-link header__burger-menu-link_type_account">Аккаунт</Link>
                    </div>
                </div>

            </div>
        </div>
    )
}

export default Navigation;
