import "./MoviesCard.css";
import React from 'react';

function MoviesCard({ movie, isSavedMovie }) {
    const countDurationTime = (data) => {
        return `${Math.floor(Math.floor(data / 1000) / 60) % 24}ч ${Math.floor(Math.floor(data / 1000) / 60) % 60}м`;
    };

    const [isLiked, setIsLiked] = React.useState(false);
    const [isDeleteButtonVisible, setIsDeleteButtonVisible] = React.useState(false);

    const handleLike = () => {
        isLiked ? setIsLiked(false) : setIsLiked(true);
    }

    function handleCardMouseOver() {
        setIsDeleteButtonVisible(true);
    }

    function handleCardMouseOut() {
        setIsDeleteButtonVisible(false);
    }



    // const saveButtonClassName = `movie__like-button 
    //     ${isLiked ? "movie__like-button_active" : ""}`;

    // console.log(saveButtonClassName);

    return (
        <li className="movie">
            <img className="movie__image" alt={`Картинка к ${movie.title}`} src={movie.image}></img>
            <h2 className="movie__title">{movie.title}</h2>
            <span className="movie__duration">
                {countDurationTime(movie.duration)}
            </span>
            <div onMouseEnter={handleCardMouseOver} onMouseLeave={handleCardMouseOut} className="movies__list-description">
                {/* <p className="movies__list-title">По волнам: Искусство звука в кино</p> */}

                {isSavedMovie ?
                    <button className={`movies__list-delete-button ${isDeleteButtonVisible 
                        ? 'movies__list-delete-button_visible' : 
                        ''}`}></button> : <button className={`movies__list-like-button ${isLiked 
                            ? 'movies__list-like-button_clicked' : ''}`} onClick={handleLike}></button>}
            </div>
            {/* <button className={saveButtonClassName} onClick={handleLike}></button> */}
        </li>
    );
}

export default MoviesCard;
