import "../Navigation/Navigation.css";
import React from "react";
import { Link } from "react-router-dom";
import Burger from "../Burger/Burger";

function Navigation({ isLoggedIn }) {

    const [isBurgerOpen, setIsBurgerOpen] = React.useState(false);

    // console.log(isBurgerOpen)

    React.useEffect(() => {
      const body = document.querySelector("body");
      console.log(body)
      isBurgerOpen
        ? body.classList.add("body_locked")
        : body.classList.remove("body_locked");
    }, [isBurgerOpen]);

    const handleBurgerClick = () => {
      isBurgerOpen ? setIsBurgerOpen(false) : setIsBurgerOpen(true);
    };

    // return (
    //     <div className="navigation">
    //         <Switch>
    //             {!isLoggedIn}
    //         </Switch>
    //     </div>
    // )

    return !isLoggedIn ? (
        <div className="menu">
            <nav className="nav">
                <div className="nav__profile">
                    <ul className="menu__list">
                        <li className="menu__item">
                        <Link className="menu__link" to="/signup">
                            Регистрация
                        </Link>
                        </li>
                        <li className="menu__item">
                        <Link className="menu__link nav__button" to="/signin">
                            Войти
                        </Link>
                        </li>
                    </ul>
                </div>
            </nav>
        </div>
        ) : (
        <>
            <Burger onClick={handleBurgerClick} isBurgerOpen={isBurgerOpen} />        
            {/* <div className="menu"> */}
            <div className={`menu ${isBurgerOpen ? "menu_opened" : ""}`}>
                    {/* <nav> */}
                        <ul className="menu__list">
                            <li className="menu__item">
                                <Link className="menu__link" to="/movies">
                                    Фильмы
                                </Link>
                            </li>
                            <li className="menu__item">
                                <Link className="menu__link" to="/saved-movies">
                                    Сохранённые фильмы
                                </Link>
                            </li>
                        </ul>
                    {/* </nav> */}
                <div className="nav__profile">
                    <nav>
                        <ul className="menu__list">
                            <li className="menu__item">
                                <Link className="menu__link menu__link_type_profile" to="/profile">
                                    Аккаунт
                                </Link>
                            </li>
                        </ul>
                    </nav>
                </div>
            </div>
        </>
    );
}

export default Navigation;
