import "../Navigation/Navigation.css";
import React from "react";
import { Link } from "react-router-dom";
import Burger from "../Burger/Burger";

function Navigation({ isLoggedIn }) {

    // const [isBurgerOpen, setIsBurgerOpen] = React.useState(false);

    // React.useEffect(() => {
    //   const body = document.querySelector(".body");
    //   isBurgerOpen
    //     ? body.classList.add("body_locked")
    //     : body.classList.remove("body_locked");
    // }, [isBurgerOpen]);

    // const handleBurgerClick = () => {
    //   isBurgerOpen ? setIsBurgerOpen(false) : setIsBurgerOpen(true);
    // };

    return isLoggedIn ? (
        <>
            {/* <Burger onClick={handleBurgerClick} isBurgerOpen={isBurgerOpen} />         */}
            <div className="menu">
                <div className="nav__menu">
                {/* <div className={`nav__menu ${isBurgerOpen ? "nav__menu_opened" : ""}`}> */}
                    <nav>
                        <ul className="nav__list">
                            <li className="nav__item">
                                <Link className="nav__link" to="/movies">
                                    Фильмы
                                </Link>
                            </li>
                            <li className="nav__item">
                                <Link className="nav__link" to="/saved-movies">
                                    Сохранённые фильмы
                                </Link>
                            </li>
                        </ul>
                    </nav>
                </div>
                <div className="nav__profile">
                    <nav>
                        <ul className="nav__list">
                            <li className="nav__item">
                                <Link className="nav__link menu__link_type_profile" to="/profile">
                                    Аккаунт
                                </Link>
                            </li>
                        </ul>
                    </nav>
                </div>
            </div>
        </>
    ) : (
        <>
            <nav className="nav">
                <div className="nav__profile">
                <ul className="nav__list">
                    <li className="nav__item">
                    <Link className="nav__link" to="/signup">
                        Регистрация
                    </Link>
                    </li>
                    <li className="nav__item">
                    <Link className="nav__link nav__button" to="/signin">
                        Войти
                    </Link>
                    </li>
                </ul>
                </div>
            </nav>
        </>
    );
}

export default Navigation;
