import "./MoviesCard.css";
import React from 'react';

function MoviesCard({ movie, isSaved}) {
    const millisecondsToTime = (mil) => {
        const totalSeconds = Math.floor(mil / 1000);
        const totalMinutes = Math.floor(totalSeconds / 60);
        const totalHours = Math.floor(totalMinutes / 60);
        const minutes = totalMinutes % 60;
        const hours = totalHours % 24;
        return `${hours}ч ${minutes}м`;
    };

    const [isLiked, setIsLiked] = React.useState(false);

    const handleLike = () => {
        isLiked ? setIsLiked(false) : setIsLiked(true);
    }


    // const saveButtonClassName = `movie__like-button 
    //     ${isLiked ? "movie__save-button_saved" : ""} 
    //     ${isSaved ? "movie__save-button_in-list" : ""}`;

    const saveButtonClassName = `movie__like-button 
        ${isLiked ? "movie__like-button_active" : ""}`;

    return (
        <li className="movie">
        <h2 className="movie__title">{movie.title}</h2>
        <span className="movie__duration">
            {millisecondsToTime(movie.duration)}
        </span>
        {/* <button className={saveButtonClassName} onClick={handleLike}></button> */}
        <button className={saveButtonClassName}></button>
        <img className="movie__image" alt={`Картинка к ${movie.title}`} src={movie.image}></img>
        </li>
    );
}

export default MoviesCard;
